measure.query:
SELECT CAST(FROM_UNIXTIME(TIME_STAMP/1000) AS CHAR) AS Time, COUNT(*) AS 'Private Chatrooms' FROM MESSAGE
WHERE EVENT =  'SERVICE_CUSTOM_MESSAGE_3' AND SOURCE_AGENT =  '$SERVICE$'
GROUP BY TIME_STAMP;

SELECT CAST(FROM_UNIXTIME(TIME_STAMP/1000) AS CHAR) AS Time, COUNT(*) AS 'Public Chatrooms' FROM MESSAGE
WHERE EVENT =  'SERVICE_CUSTOM_MESSAGE_4' AND SOURCE_AGENT =  '$SERVICE$'
GROUP BY TIME_STAMP;
